    public static float sigmoid(float x) {
        int id = (int) (x * 51.2 + 512);
        return sigmoid_values[((id >= 0 && id < 1024) ? id : 0) + ((id >= 1024) ? 1024 : 0)];
    }

    public static float sum(float[] values, float[] weights) {
        float sum = 0;
        for (int i = 0; i < values.length; i++) {
            sum += values[i] * weights[i];
        }
        return sum;
    }

    public static float transfer_derivative(float output) {
        return output * (1 - output);
    }

